import 'Components/System/Popup/popup.scss';
import { Link } from 'react-router-dom';
import { PopupProps } from 'Components/System/Popup/popupInterface';
import PopupText from 'Components/System/Popup/PopupText';

const Popup: React.FC<PopupProps> = ({ nextQuestion, isCorrect, counter, questionLimit, correctCounter, answerKey, explanation, characterItem }) => {
  const iconClass = isCorrect ? 'oxIcon correct' : 'oxIcon inCorrect';

  if (counter < questionLimit) {          
    return (
        <div className='popup'>
          <div className='popupContent'>
            <PopupText isCorrect={isCorrect} answerKey={answerKey} explanation={explanation} characterItem={characterItem} />
            <button onClick={nextQuestion} className='nextButton'>次の問題へ</button>
          </div>
        </div>
      );
  }else{
    return(
      <div className='popup'>
        <div className='popupContent'>
          <PopupText isCorrect={isCorrect} answerKey={answerKey} explanation={explanation} characterItem={characterItem} />
          <Link
            to='/Result/Result'
            state= {{ correctCounter, questionLimit }}
            className='nextButton'
          >
            結果発表
          </Link>
        </div>
      </div>
    );
  }
};

export default Popup;
