import 'src/Components/System/system.scss';
import { Link } from 'react-router-dom';
import { PopupProps } from 'src/Components/System/systemInterface';

const Popup: React.FC<PopupProps> = ({ nextQuestion, isCorrect, counter, questionLimit }) => {
  if (counter < questionLimit) {          
    return (
        <div className="popup">
          <div className="popup-content">
            <p>{ isCorrect ? '正解！' : '残念！' }</p>
            <button onClick={nextQuestion}>次の問題へ</button>
          </div>
        </div>
      );
  }else{
    return(
      <>
      <div className="popup">
        <div className="popup-content">
          <p>{ isCorrect ? '正解！' : '残念！' }</p>
          <Link to='/'>結果発表</Link>
        </div>
      </div>
      </>
    );
  }
};

export default Popup;
