    useEffect(() => {
        if (clickData && !showPopup) {
            if (characterItem && clickData === characterItem.gender) {
                console.log('正解！')
                setShowPopup(true);
                setIsCorrect(true);
            }else {
                console.log('残念！')
                setShowPopup(true);
                setIsCorrect(false);
            };
        }
    },[clickData]);

    return (null
    );
};

export default CheckAnswer;
