import React, { useEffect } from "react";

interface CharacterData {
    name: string;
    gender: string;
}

interface CheckAnswerProps {
    clickData: string;
    characterItem: CharacterData | null;
    onResult: (isCorrect: boolean) => void;
    showPopup: boolean;
    onShowPopup: (show: boolean) => void;
    isCorrect: boolean;
}

const CheckAnswer: React.FC<CheckAnswerProps> = ({ clickData, characterItem, onResult, showPopup, onShowPopup, isCorrect }) => {
    useEffect(() => {
        if (clickData && !showPopup) {
            if (characterItem && clickData === characterItem.gender) {
                console.log('正解！')
                onShowPopup(true);
                onResult(true);
            }else {
                console.log('残念！')
                onShowPopup(true);
                onResult(false);
            };
        }
    },[clickData]);

    return (null
        // <p>{ isCorrect ? '正解！' : '残念！' }
    );
};

export default CheckAnswer;
