import React, { useEffect } from "react";
import { CheckAnswerProps } from "src/Components/System/interfaceTypes";

const CheckAnswer: React.FC<CheckAnswerProps> = ({ clickData, characterItem, setIsCorrect, showPopup, setShowPopup, isCorrect }) => {
    useEffect(() => {
        if (clickData && !showPopup) {
            if (characterItem && clickData === characterItem.gender) {
                console.log('正解！')
                setShowPopup(true);
                setIsCorrect(true);
            }else {
                console.log('残念！')
                setShowPopup(true);
                setIsCorrect(false);
            };
        }
    },[clickData]);

    return (
        <>
        {showPopup && 
            <p>{ isCorrect ? '正解！' : '残念！' }</p>
        }
        </>
    );
};

export default CheckAnswer;
