import { Link } from 'react-router-dom';

interface PopupProps {
    clickData: string;
    nextQuestion: () => void;
    isCorrect: boolean;
    counter: number;
    questionLimit: number;
  }

const Popup: React.FC<PopupProps> = ({ nextQuestion, isCorrect, counter, questionLimit }) => {
  if (counter < questionLimit) {          
    return (
        <div className="popup">
          <div className="popup-content">
            <p>{ isCorrect ? '正解！' : '残念！' }</p>
            <button onClick={nextQuestion}>次の問題へ</button>
          </div>
        </div>
      );
  }else{
    return(
      <>
      <div className="popup">
        <div className="popup-content"><CheckAnswer
          clickData={clickData}
          characterItem={characterItem}
          onResult={setIsCorrect}
          showPopup={showPopup}
          onShowPopup={setShowPopup}
          isCorrect={isCorrect}
        />
        </div>
      </div>
      <Link to='/'>結果発表</Link>
      </>
    );
  }
};

export default Popup;
