import 'Components/System/system.scss';
import { PopupTextProps } from 'Components/System/Popup/popupInterface';

const PopupText: React.FC<PopupTextProps> = ({ isCorrect, explanation }) => {
    const iconClass = isCorrect ? 'oxIcon correct' : 'oxIcon inCorrect';

    return (
        <>
            <p className={ iconClass }>{ isCorrect ? 'O' : 'X' }</p>
            <p className='popupText'>{ isCorrect ? '正解！' : '残念！' }</p>
            <p className='explantionText'>解答・解説：{explanation}</p>
        </>
    );
};

export default PopupText;
