import React, { useEffect } from 'react';
import { CheckAnswerProps } from 'src/Components/System/systemInterface';

const CheckAnswer: React.FC<CheckAnswerProps> = ({ clickData, characterItem, setIsCorrect, showPopup, setShowPopup, correctCounter, setCorrectCounter, selects, answerKey }) => {
    useEffect(() => {
        if (clickData && !showPopup && characterItem) {
            const correctAnswer = characterItem.selects[characterItem.answerKey];
            const isCorrectAnswer = clickData === correctAnswer
            // const isCorrectAnswer = Array(characterItem.answer)
            //     ? characterItem.answer.includes(clickData)
            //     : clickData === characterItem.answer;

            if (isCorrectAnswer) {
                console.log('正解！')
                setShowPopup(true);
                setIsCorrect(true);
                setCorrectCounter(prevCorrectCounter => prevCorrectCounter + 1);
            }else {
                console.log('残念！')
                setShowPopup(true);
                setIsCorrect(false);
            };
        }
    },[clickData]);

    return (
        null
    );
};

export default CheckAnswer;
