import React, { useEffect } from "react";

interface CheckAnswerProps {
    clickData: string;
    characterItem: CharacterData | null;
    showPopup: boolean;
    setShowPopup: (show: boolean) => void;
    isCorrect: boolean;
    setIsCorrect: (isCorrect: boolean) => void;
}

const CheckAnswer: React.FC<CheckAnswerProps> = ({ clickData, characterItem, setIsCorrect, showPopup, setShowPopup, isCorrect }) => {
    useEffect(() => {
        if (clickData && !showPopup) {
            if (characterItem && clickData === characterItem.gender) {
                console.log('正解！')
                setShowPopup(true);
                setIsCorrect(true);
            }else {
                console.log('残念！')
                setShowPopup(true);
                setIsCorrect(false);
            };
        }
    },[clickData]);

    return (
        <p>{ isCorrect ? '正解！' : '残念！' }</p>
    );
};

export default CheckAnswer;
