    characterItem: CharacterData | null;
    onResult: (isCorrect: boolean) => void;
    onShowPopup: (show: boolean) => void;
}

const CheckAnswer: React.FC<CheckAnswerProps> = ({ clickData, haracterItem, onResult, onShowPopyp }) => {
    useEffect(() => {
        if (ClickData) {
            if (characterItem && ClickData === characterItem.gender) {
                console.log('正解！')
                setShowPopup(true);
                setIsCorrect(true);
            }else {
                console.log('残念！')
