interface PopupProps {
    nextQuestion: () => void;
    isCorrect: boolean;
    count: number;
  }

const Popup: React.FC<PopupProps> = ({ nextQuestion, isCorrect }) => {
    return (
      <div className="popup">
        <div className="popup-content">
          <p>{ isCorrect ? '正解！' : '残念！' }</p>
          <button onClick={nextQuestion}>次の問題へ</button>
        </div>
      </div>
    );
};

export default Popup;
