import 'src/Components/SelectGenderButton/SGButton.scss';
import { useCallback, MouseEventHandler } from "react";
// import { SGList } from 'src/Components/SelectGenderButton/SGList';
import { ClickData } from 'src/Components/SelectGenderButton/SGInterface';

const CustomButton: React.FC<ClickData> = ({ characterItem, clickData, showPopup }) => {
    const handleClick: MouseEventHandler<HTMLButtonElement>  = (event) => {
        const index = parseInt(event.currentTarget.dataset.index || '0',10);
        const selectGender = event.currentTarget.dataset.gender || '';
        console.log(selectGender+'がクリックされました');
        clickData(selectGender);
    };

    return (
        <div className='selectButton'>
        <ul>
            {[characterItem.select1, characterItem.select2, characterItem.select3, characterItem.select4].map((gender, index) => (
            <button
            key={ index }
            className='customButton'
            onClick={ handleClick }
            data-index={ gender }
            disabled={showPopup}
            >
                <li key={ gender }>
                    { gender }
                </li>
            </button>
            ))}
        </ul>
        </div>
    );
};

export default CustomButton;
