interface PopupProps {
    onClose: () => boolean;
    isCorrect: boolean;
  }

const Popup: React.FC<PopupProps> = ({ onClose, isCorrect }) => {
    return (
      <div className="popup">
        <div className="popup-content">
          <p>{ isCorrect ? '正解！' : '残念！' }</p>
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    );
};

export default Popup;
